PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `cooldowns` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `cooldownKey` VARCHAR(255) NOT NULL UNIQUE, `expiresAt` DATETIME NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `user_stats` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userId` VARCHAR(255) NOT NULL, `guildId` VARCHAR(255) NOT NULL, `commandsUsed` INTEGER DEFAULT 0, `messagesCount` INTEGER DEFAULT 0, `lastActive` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `scheduled_messages` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userId` VARCHAR(255) NOT NULL, `guildId` VARCHAR(255) NOT NULL, `channelId` VARCHAR(255) NOT NULL, `targetUserIds` TEXT NOT NULL, `scheduledTime` DATETIME NOT NULL, `message` TEXT NOT NULL, `sent` TINYINT(1) DEFAULT 0, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `warnings` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userId` VARCHAR(255) NOT NULL, `guildId` VARCHAR(255) NOT NULL, `moderatorId` VARCHAR(255) NOT NULL, `reason` TEXT NOT NULL, `timestamp` DATETIME NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `locked_channels` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `channelId` VARCHAR(255) NOT NULL, `guildId` VARCHAR(255) NOT NULL, `lockedBy` VARCHAR(255) NOT NULL, `reason` TEXT NOT NULL, `lockedAt` DATETIME NOT NULL, `unlockAt` DATETIME, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `guild_configs` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `guildId` VARCHAR(255) NOT NULL UNIQUE, `modChannelId` VARCHAR(255), `publicModChannelId` VARCHAR(255), `muteRoleId` VARCHAR(255), `autoModEnabled` TINYINT(1) DEFAULT 0, `logBans` TINYINT(1) DEFAULT 1, `logKicks` TINYINT(1) DEFAULT 1, `logTimeouts` TINYINT(1) DEFAULT 1, `logWarnings` TINYINT(1) DEFAULT 1, `logChannelLocks` TINYINT(1) DEFAULT 1, `logMessageDeletes` TINYINT(1) DEFAULT 1, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE INDEX `cooldowns_expires_at` ON `cooldowns` (`expiresAt`);
CREATE UNIQUE INDEX `user_stats_user_id_guild_id` ON `user_stats` (`userId`, `guildId`);
CREATE INDEX `scheduled_messages_scheduled_time_sent` ON `scheduled_messages` (`scheduledTime`, `sent`);
CREATE INDEX `scheduled_messages_guild_id` ON `scheduled_messages` (`guildId`);
CREATE INDEX `warnings_user_id_guild_id` ON `warnings` (`userId`, `guildId`);
CREATE INDEX `warnings_guild_id` ON `warnings` (`guildId`);
CREATE INDEX `warnings_timestamp` ON `warnings` (`timestamp`);
CREATE UNIQUE INDEX `locked_channels_channel_id_guild_id` ON `locked_channels` (`channelId`, `guildId`);
CREATE INDEX `locked_channels_guild_id` ON `locked_channels` (`guildId`);
CREATE INDEX `locked_channels_unlock_at` ON `locked_channels` (`unlockAt`);
CREATE UNIQUE INDEX `guild_configs_guild_id` ON `guild_configs` (`guildId`);
COMMIT;
